	public static void m2(int n)
	{
		int d = 0;
		for (int i = 0;i <= n;i++)
		{
			A[i] = A[i] * 2 + d;
			d = A[i] / 10;
			A[i] %= 10;
		}
		return;
	}

	public static int Main()
	{
		int n;
		n = Integer.parseInt(ConsoleInput.readToWhiteSpace(true));
		for (int i = 1;i <= n;i++)
		{
			m2(i);
		}

		int i = 999;
		while (A[i] == 0)
		{
			i--;
		}
		while (i >= 0)
		{
			System.out.print(A[i],i--);
		}
		return 0;
	}

