	public static int len = 1;

	public static void cheng()
	{
		int i = 0;
		for (i = 0;i < len;i++)
		{
			sum[i] = sum[i] * 2;
		}
		for (i = 0;i < len - 1;i++)
		{
			if (sum[i] >= 10)
			{
				sum[i] = sum[i] - 10;
				sum[i + 1]++;
			}
		}
		if (sum[len - 1] >= 10)
		{
			sum[i] = sum[i] - 10;
			sum[i + 1]++;
			len++;
		}
		return;
	}

	public static int Main()
	{
		int n;
		int i = 0;
		n = Integer.parseInt(ConsoleInput.readToWhiteSpace(true));
		for (i = 0;i < n;i++)
		{
			cheng();
		}
		for (i = len - 1;i >= 0;i--)
		{
		   System.out.print(sum[i]);
		}
		return 0;
	}

